CREATE TABLE IF NOT EXISTS `sync_record` ( `record_id` int(11) NOT NULL auto_increment, `uuid` char(36) NOT NULL, `creator` char(36) default NULL, `database_version` char(8) default NULL, `timestamp` datetime default NULL, `retry_count` int(11) default NULL, `state` varchar(20) default NULL, `payload` longtext, `contained_classes` varchar(1000) default '', `original_uuid` varchar(36) NOT NULL, PRIMARY KEY (`record_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `sync_import` ( `record_id` int(11) NOT NULL auto_increment, `uuid` char(36) NOT NULL, `creator` char(36) default NULL, `database_version` char(8) default NULL, `timestamp` datetime default NULL, `retry_count` int(11) default NULL, `state` varchar(20) default NULL, `payload` longtext, `error_message` varchar(255) default NULL, PRIMARY KEY (`record_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `sync_server` ( `server_id` int(11) NOT NULL auto_increment, `nickname` varchar(255) default NULL, `address` varchar(255) NOT NULL, `server_type` varchar(20) NOT NULL, `username` varchar(255) default NULL, `password` varchar(255) default NULL, `uuid` char(36) default NULL, `last_sync` datetime default NULL, `last_sync_state` varchar(50) default NULL, `disabled` tinyint(1) NOT NULL, `child_username` varchar(50) default '', PRIMARY KEY (`server_id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
